/* spOmisionPersonalPropValor */
if exists (select * from sysobjects where id = object_id('dbo.spOmisionPersonalPropValor') and sysstat & 0xf = 4) drop procedure dbo.spOmisionPersonalPropValor
GO
CREATE PROCEDURE spOmisionPersonalPropValor
--//WITH ENCRYPTION
AS BEGIN
  DECLARE
    @Empresa	char(5),
    @Personal	char(10)

  TRUNCATE TABLE PersonalPropValor
  DECLARE crEmpresa CURSOR FOR
  SELECT Empresa FROM Empresa

  OPEN crEmpresa
  FETCH NEXT FROM crEmpresa INTO @Empresa
  WHILE @@FETCH_STATUS <> -1 AND @@Error = 0 
  BEGIN
    IF @@FETCH_STATUS <> -2 
    BEGIN
      INSERT INTO PersonalPropValor (Rama,Cuenta,Propiedad,Valor) 
         SELECT 'EMP', @Empresa, Propiedad, porOmision
          FROM PersonalProp
        WHERE NivelEmpresa = 1
    END
    FETCH NEXT FROM crEmpresa INTO @Empresa
  END
  CLOSE crEmpresa
  DEALLOCATE crEmpresa
  
  DECLARE crPersonal CURSOR FOR
   SELECT Personal FROM Personal
  
  OPEN crPersonal
  FETCH NEXT FROM crPersonal INTO @Personal
  WHILE @@FETCH_STATUS <> -1 AND @@Error = 0 
  BEGIN
    IF @@FETCH_STATUS <> -2 
    BEGIN
      INSERT INTO PersonalPropValor (Rama,Cuenta,Propiedad,Valor) 
         SELECT 'PER', @Personal, Propiedad, porOmision
          FROM PersonalProp
        WHERE NivelPersonal = 1
    END
    FETCH NEXT FROM crPersonal INTO @Personal
  END
  CLOSE crPersonal
  DEALLOCATE crPersonal
end
