CREATE FUNCTION JSON2XML
(
	@JSON VARCHAR(MAX)
)
RETURNS XML
AS
BEGIN
	DECLARE @XML XML
	SET @XML = CAST(dbo.CLRJSONTOXML(@JSON) AS XML)
	RETURN @XML
END
GO
CREATE FUNCTION [dbo].[XML2JSON]  
(
	@XML XML
)
RETURNS NVARCHAR(MAX)
AS
BEGIN
	DECLARE @JSON NVARCHAR(MAX)
	--SET @JSON = dbo.String2JSON(dbo.CLRXMLTOJSON(CAST(@XML AS NVARCHAR(MAX))))
	SET @JSON = dbo.CLRXMLTOJSON(CAST(@XML AS NVARCHAR(MAX)))
	RETURN @JSON
END
