IF EXISTS (SELECT id FROM sysobjects WHERE id = object_id('dbo.spISIntelisisMovilMovilUsuarioCfgListado') AND type = 'P') DROP PROCEDURE dbo.spISIntelisisMovilMovilUsuarioCfgListado
