  END
  
  SELECT TOP 1 @Situacion = Situacion
    FROM CampanaTipoSituacion
   WHERE Orden IS NOT NULL AND AccionMovil = 'Sincronizado'
   ORDER BY Orden

  SELECT
		 Visitas.RID IdVisita,
