CREATE PROCEDURE dbo.spISIntelisisMovilCobroAlta
		@ID				int,
		@iSolicitud		int,
		@Version		float,
		@Resultado		varchar(max) = NULL OUTPUT,
		@Ok				int = NULL OUTPUT,    
		@OkRef			varchar(255) = NULL OUTPUT
--//WITH ENCRYPTION
AS
BEGIN
  DECLARE
    /***    DATOS A OBTENER DE XML    ***/
    @Usuario        varchar(10),
    @Contrasena     varchar(25),
    @Cliente        varchar(10),
    @FormaPago      varchar(50),
    @Importe        money,
    @Moneda         varchar(10),
    @Concepto       varchar(50),
    @Referencia     varchar(50),
    @Observaciones  varchar(100),
