CREATE VIEW SysCampo
--//WITH ENCRYPTION
AS
SELECT "Tabla" = t.name, "Campo" = c.name , "ColID" = c.ColID, "EsCalculado" = c.IsComputed
  FROM sysobjects t, syscolumns c
 WHERE t.id = c.id AND t.type = 'U'
