/****** Object   StoredProcedure [dbo].[spObtenerTasaCuotaIEPS]    Script Date  02/18/2013 06 31 01 ******/
IF NOT EXISTS (SELECT * FROM sys.objects WHERE object_id = OBJECT_ID(N'[dbo].[spObtenerTasaCuotaIEPS]') AND type in (N'P', N'PC'))
BEGIN
EXEC dbo.sp_executesql @statement = N'CREATE PROCEDURE [dbo].[spObtenerTasaCuotaIEPS]
	@reporte int,
	@ejercicio int,
	@tipo varchar(5), --indica si desea la tasa o la cuota
	@periodo int = 0,
	@idclasificacionieps int = 0 --Este campo por default tiene como valor 0
WITH ENCRYPTION
AS
BEGIN
DECLARE @tasa as float, @cuota as float
DECLARE @esxperiodo as bit
	
	if lower(@tipo) = ''tasa''
	BEGIN
	--Revisar si el reporte tiene configuracion de tasa por periodo
		select @esxperiodo = isnull(tasaxperiodo,0) from reportes where reporte = @reporte
		
		--Si @articulo es diferente de 0, anexar @articulo en el Where			
		if @esxperiodo = 1 select @tasa = isnull(Tasa,0) from Iepstasacuota where reporte = @reporte and ejercicio = @ejercicio and periodo = @periodo and IdClasificacionIeps = @idclasificacionieps
		else select @tasa = isnull(Tasa,0) from Iepstasacuota where reporte = @reporte and ejercicio = @ejercicio  and IdClasificacionIeps = @idclasificacionieps --and periodo = 1

		select @tasa as tasa
	END
	ELSE
	BEGIN
	--Revisar si el reporte tiene configuracion de cuota por periodo
		select @esxperiodo = isnull(cuotaxperiodo,0) from reportes where reporte = @reporte
		
		if @esxperiodo = 1 select @cuota = isnull(cuota,0) from Iepstasacuota where reporte = @reporte and ejercicio = @ejercicio and periodo = @periodo and IdClasificacionIeps = @idclasificacionieps
		else select @cuota = isnull(cuota,0) from Iepstasacuota where reporte = @reporte and ejercicio = @ejercicio and IdClasificacionIeps = @idclasificacionieps --and periodo = 1
		
		select @cuota as cuota

	END
	--select @tasa
END
' 
END
GO
