	SET @lcFiltro = @lcFiltro + ' AND ActivosF.Clave ='''+@ClaveAc+''''
	
IF LOWER(ISNULL(@ConSaldo,'no'))='si'
	SET @lcFiltroSaldo = ' WHERE DeduccionFis<>0'

