						dbo.fnMFAMesesDeUso(Clave,'''+@Empresa+''',CAST('''+@fecha_ope+''' as DATETIME)) as mesesUso,
                        ISNULL(DepFiscalAct,0) DepFiscalAct,ISNULL(DepFinanAct,0)DepFinanAct,MontoOrig
