CREATE PROCEDURE sp_layout_importar_costomovs
							@usuario	varchar(20),
							@pEmpresa varchar(5), --anex
							@pEjercicio smallint
							
WITH ENCRYPTION
AS BEGIN  
  DECLARE
    @log_id				int,
    @error				int,
    @error_mensaje		varchar(255),
    @conteo				int
  
  SELECT @usuario = UPPER(@usuario)
  SELECT @error			= NULL, 
		 @error_mensaje = NULL,
		 @conteo		= 0

  --BUG26056
  EXEC sp_layout_log_in @usuario, 'importar costo', 'importando', @pEmpresa, @log_id OUTPUT
   
